#include <stdio.h>
#include <stdlib.h>

// Function to compute the dot product of two vectors
double dotProduct(double* vector1, double* vector2, int length) {
    double result = 0.0;
    for (int i = 0; i < length; i++) {
        result += vector1[i] * vector2[i];
    }
    return result;
}

