    double* vector2 = (double*)malloc(length * sizeof(int));

    // Input elements of the first vector
    printf("Enter elements of the first vector:\n");
    for (int i = 0; i < length; i++) {
        scanf("%lf", &vector1[i]);
    }

    // Input elements of the second vector
    printf("Enter elements of the second vector:\n");
    for (int i = 0; i < length; i++) {
        scanf("%lf", &vector2[i]);
    }

    // Compute the dot product
    double result = dotProduct(vector1, vector2, length);

    // Output the result
    printf("The dot product of the two vectors is: %.2f\n", result);

